import * as actionTypes from "../actions/singleUserActions";

const intialState = {
  singleUserId: null,
  isLoading: false,
  error: null,
  userdata: null
};

const singleUserReducer = (state = intialState, action) => {
  switch (action.type) {
    case actionTypes.GET_USER_NAME:
      return {
        ...state,
        singleUserId: action.id
      };
    case actionTypes.FETCH_SINGLE_USERS_BEGIN:
      return {
        ...state,
        isLoading: true
      };
    case actionTypes.FETCH_SINGLE_USERS_SUCCESS:
      return {
        ...state,
        isLoading: false,
        userdata: action.data
      };
    case actionTypes.FETCH_SINGLE_USERS_ERROR:
      return {
        ...state,
        error: action.error,
        isLoading: false
      };
    default:
      return state;
  }
};

export default singleUserReducer;
