import {
  FETCH_USERS_PENDING,
  FETCH_USERS_SUCCESS,
  FETCH_USERS_ERROR
} from "./actions";

const intialState = {
  pending: false,
  users: [],
  error: null
};

const rootReducer = (state = intialState, action) => {
  switch (action.type) {
    case FETCH_USERS_PENDING:
      return {
        ...state,
        pending: true
      };
    case FETCH_USERS_SUCCESS:
      return {
        ...state,
        data: action.payload
      };
    case FETCH_USERS_ERROR:
      return {
        ...state,
        error: action.error
      };
  }
  return state;
};

export default rootReducer;
