import React, { Component } from "react";
import "./App.css";
import { fetchUsers } from "./Store/actions/fetchUserActions";
import { fetchSingleUser } from "./Store/actions/singleUserActions";
import { connect } from "react-redux";

class App extends Component {
  componentDidMount() {
    this.props.dispatch(fetchUsers());
  }

  onChangeHandler = event => {
    const select = event.target;
    const id = select.children[select.selectedIndex].id;
    console.log(id);
    this.props.dispatch(fetchSingleUser(id));
  };

  render() {
    return (
      <div className="App">
        <select onChange={this.onChangeHandler}>
          {this.props.loading && <option>Loading</option>}
          {this.props.users.map((i, index) => {
            return (
              <option id={i.id} key={i.id} value={i.name}>
                {i.name}
              </option>
            );
          })}
        </select>
        {this.props.singleUserLoading ? (
          "loading"
        ) : (
          <h1>{this.props.userid}</h1>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.allUsers.users,
    loading: state.allUsers.loading,
    error: state.allUsers.error,
    userid: state.fetchSingleUser.singleUserId
  };
};

export default connect(mapStateToProps)(App);
