import {
  FETCH_USERS_BEGIN,
  FETCH_USERS_SUCCESS,
  FETCH_USERS_ERROR
} from "./actions";

const intialState = {
  singleUser: "",
  pending: false,
  users: [],
  error: null
};

const rootReducer = (state = intialState, action) => {
  switch (action.type) {
    case FETCH_USERS_BEGIN:
      return {
        ...state,
        pending: true,
        error: null
      };
    case FETCH_USERS_SUCCESS:
      return {
        ...state,
        data: action.payload
      };
    case FETCH_USERS_ERROR:
      return {
        ...state,
        pending: false,
        error: action.error,
        users: []
      };
    default:
      return state;
  }
};

export default rootReducer;
